Vector3f integrate(const Ray& ray, const Scene& scene, int depth) {
    if (depth > MAX_BOUNCES) return Vector3f(0.0);

    Intersection isect = scene.intersect(ray);
    if (!isect.hit) return scene.getEnvironmentLight(ray);

    // Echantillonnage de la BRDF
    Vector3f wi = isect.material->sampleBRDF(ray.direction, isect.normal);
    float pdf = isect.material->pdf(ray.direction, wi, isect.normal);

    // Récursion Monte Carlo
    Ray next_ray(isect.point, wi);
    Vector3f incoming_light = integrate(next_ray, scene, depth + 1);

    return isect.emitted() + (isect.material->eval(ray.direction, wi, isect.normal) 
           * incoming_light * std::max(0.0f, dot(wi, isect.normal))) / pdf;
}
